import React from 'react';
import CharacterItem from './CharacterItem';


const CharacterGrid = ({items, isLoading}) => {
  return isLoading ? ( <h1 className= 'text-white-700 text-base '>Loading...</h1>) : (<section className= " text-white text-grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-6 grid gap-x-1">
      {items.map((item) => (
      <CharacterItem key={item.char_id} >{item.name}</CharacterItem>
      ))}
  </section>
  )
};

export default CharacterGrid;
